package sorting;

import java.util.*;

/**
 * Created by srathi on 11/5/16.
 *
 * Given a string, sort it in decreasing order based on the frequency of characters.

 Example 1:
 Input:
 "tree"
 Output:
 "eert"
 Explanation:
 'e' appears twice while 'r' and 't' both appear once.
 So 'e' must appear before both 'r' and 't'. Therefore "eetr" is also a valid answer.

 Example 2:
 Input:
 "cccaaa"
 Output:
 "cccaaa"

 Explanation:
 Both 'c' and 'a' appear three times, so "aaaccc" is also a valid answer.
 Note that "cacaca" is incorrect, as the same characters must be together.

 Example 3:
 Input:
 "Aabb"

 Output:
 "bbAa"

 Explanation:
 "bbaA" is also a valid answer, but "Aabb" is incorrect.
 Note that 'A' and 'a' are treated as two different characters.

 */

class DArray
{
    private Obj[] theArray; // ref to array theArray
    private int nElems; // number of data items
    //-----------------------------------------------------------
    public DArray(Obj[] arr) // constructor
    {
        theArray = arr; // create array
        nElems = arr.length;
    }

    //-----------------------------------------------------------
    public void display() // displays array contents
    {
        for(int j=0; j<nElems; j++) // for each element,
            System.out.print(theArray[j].ch + " " ); // display character
        System.out.println("");
        for(int j=0; j<nElems; j++) // for each element,
            System.out.print(theArray[j].frequency + " " ); // display frequency
        System.out.println("");
    }
    //-----------------------------------------------------------
    public Obj[] mergeSort() // called by main()
    { // provides workspace
        Obj[] workSpace = new Obj[nElems];
        recMergeSort(workSpace, 0, nElems-1);
        return workSpace;
    }
    //-----------------------------------------------------------
    private void recMergeSort(Obj[] workSpace, int lowerBound,
                              int upperBound)
    {
        if(lowerBound == upperBound) // if range is 1,
            return; // no use sorting
        else
        { // find midpoint
            int mid = (lowerBound+upperBound) / 2; // sort low half
            recMergeSort(workSpace, lowerBound, mid); // sort high half
            recMergeSort(workSpace, mid+1, upperBound); // merge them
            merge(workSpace, lowerBound, mid+1, upperBound);
        } // end else
    } // end recMergeSort()
    //-----------------------------------------------------------
    private void merge(Obj[] workSpace, int lowPtr,
                       int highPtr, int upperBound)
    {
        int j = 0; // workspace index
        int lowerBound = lowPtr;
        int mid = highPtr-1;
        int n = upperBound-lowerBound+1; // # of items
        while(lowPtr <= mid && highPtr <= upperBound)
            if( theArray[lowPtr].frequency > theArray[highPtr].frequency )
                workSpace[j++] = theArray[lowPtr++];
            else
                workSpace[j++] = theArray[highPtr++];
        while(lowPtr <= mid)
            workSpace[j++] = theArray[lowPtr++];
        while(highPtr <= upperBound)
            workSpace[j++] = theArray[highPtr++];
        for(j=0; j<n; j++)
            theArray[lowerBound+j] = workSpace[j];
    } // end merge()
//-----------------------------------------------------------
} // end class DArray
////////////////////////////////////////////////////////////////

class Obj
{
    Character ch;
    int frequency;
}

public class SortByFrequencyLeetCode {


    public static void main(String[] args) {

        String input  = "\"\"";
        if (input.equals(new String(""))|| input.isEmpty() || input == null)
            return;
        String output = "";
        Map<Character, Integer> map = new HashMap<>();
        for (int i =0; i<input.length(); i++)
        {
            Character ch = input.charAt(i);
            if (map.containsKey(ch))
            {
                int count = map.get(ch);
                count++;
                map.put(ch, count);
            }
            else
                map.put(ch,1);
        }

        //get the map values into an array
        Obj[] arr = new Obj[map.size()];

        int i =0;

        for (Map.Entry<Character, Integer> entry : map.entrySet()) {
            Character key = entry.getKey();
            Integer value = entry.getValue();
            Obj obj = new Obj();
            obj.ch = key;
            obj.frequency = value;
            arr[i] = obj;
            i++;
        }


        DArray dArray = new DArray(arr);
        dArray.display();
        arr = dArray.mergeSort();
        dArray.display();
        for (int k=0; k<arr.length; k++)
        {
            Obj temp = arr[k];

            String repeated = new String(new char[temp.frequency]).replace('\0', temp.ch);
            output += repeated;
        }
        System.out.println(output);


    }

}
